import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import "../styles.css";

const socket = io("http://localhost:5001"); // Ensure this matches backend port

const Video = () => {
    const localVideoRef = useRef(null);
    const remoteVideoRef = useRef(null);
    const peerConnection = useRef(null);
    const [partnerId, setPartnerId] = useState(null);

    useEffect(() => {
        socket.emit("find_match"); // Request a match on mount

        socket.on("match_found", (partner) => {
            setPartnerId(partner);
            initiateCall(partner);
        });

        socket.on("offer", async ({ sdp, sender }) => {
            if (!peerConnection.current) {
                peerConnection.current = createPeerConnection(sender);
            }
            await peerConnection.current.setRemoteDescription(new RTCSessionDescription(sdp));
            const answer = await peerConnection.current.createAnswer();
            await peerConnection.current.setLocalDescription(answer);
            socket.emit("answer", { sdp: answer, target: sender });
        });

        socket.on("answer", async ({ sdp }) => {
            if (peerConnection.current) {
                await peerConnection.current.setRemoteDescription(new RTCSessionDescription(sdp));
            }
        });

        socket.on("ice-candidate", ({ candidate }) => {
            peerConnection.current?.addIceCandidate(new RTCIceCandidate(candidate));
        });

        socket.on("disconnect_peer", () => {
            if (remoteVideoRef.current) remoteVideoRef.current.srcObject = null;
            peerConnection.current?.close();
            peerConnection.current = null;
            socket.emit("find_match"); // Rejoin queue automatically
        });

        return () => {
            socket.off("match_found");
            socket.off("offer");
            socket.off("answer");
            socket.off("ice-candidate");
            socket.off("disconnect_peer");
        };
    }, []);

    const initiateCall = async (partner) => {
        peerConnection.current = createPeerConnection(partner);
        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        localVideoRef.current.srcObject = stream;
        stream.getTracks().forEach(track => peerConnection.current.addTrack(track, stream));
    };

    const createPeerConnection = (partner) => {
        const pc = new RTCPeerConnection({
            iceServers: [{ urls: "stun:stun.l.google.com:19302" }]
        });

        pc.onicecandidate = (event) => {
            if (event.candidate) {
                socket.emit("ice-candidate", { candidate: event.candidate, target: partner });
            }
        };

        pc.ontrack = (event) => {
            remoteVideoRef.current.srcObject = event.streams[0];
        };

        return pc;
    };

    return (
        <div className="video-container">
            <video ref={localVideoRef} autoPlay playsInline muted className="local-video" />
            <video ref={remoteVideoRef} autoPlay playsInline className="remote-video" />
        </div>
    );
};

export default Video;
